sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

         var rightNow = new GlideDateTime();
		 var waitingApproval = new GlideRecord ('sys_created_on');

		waitingApproval.addQuery('sys_created_on','<=',rightNow);
		waitingApproval.addQuery('state','=',14);
		waitingApproval.addQuery('u_requested_for',current.u_requested_for);

		waitingApproval.query();

		template.print("You have this many NeedIt Application awaiting approval" + (waitingApproval.getRowCount() -1));

})(current, template, email, email_action, event);
